import { createSlice } from '@reduxjs/toolkit'

const khoaThiReducer = createSlice({
  name: 'truong',
  initialState: { id: 0, datas: [] },
  reducers: {
    setTruongId: (state, action) => {
      return { ...state, id: action.payload }
    },
    setTruongs: (state, action) => {
      return { ...state, datas: action.payload }
    },
  },
})
const { actions, reducer } = khoaThiReducer
export const { setTruongId, setTruongs } = actions
export default reducer
