import { createSlice } from '@reduxjs/toolkit'

const hinhThucDaoTaoReducer = createSlice({
  name: 'hinhThucDaoTao',
  initialState: { id: 0, datas: [] },
  reducers: {
    setHinhThucDaoTaoId: (state, action) => {
      return { ...state, id: action.payload }
    },
    setHinhThucDaoTaos: (state, action) => {
      return { ...state, datas: action.payload }
    },
  },
})
const { actions, reducer } = hinhThucDaoTaoReducer
export const { setHinhThucDaoTaoId, setHinhThucDaoTaos } = actions
export default reducer
