/* eslint-disable max-lines-per-function */
import { Button } from 'primereact/button'
import { Calendar } from 'primereact/calendar'
import { Dropdown } from 'primereact/dropdown'
import { Toolbar } from 'primereact/toolbar'
import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
// import ComboDropdownKhoaThi from '../../components/form/ComboDropdownKhoaThi'
import Table from '../../components/layout/Table'
// import reportProcessor from '../../processor/reportProcessor'
// import truongProcessor from '../../processor/truongProcessor'
import { dateToDDMMYY, DDMMYYToDate } from '../../utils/date'
import { exportExcel } from '../../utils/excel'

const date = new Date()
const y = date.getFullYear()
const m = date.getMonth()
const firstDay = new Date(y, m, 1)
const lastDay = new Date(y, m + 1, 0)
const firstDayStr = dateToDDMMYY(firstDay)
const lastDayStr = dateToDDMMYY(lastDay)
const mappingExport = {
  hoTen: 'Họ tên',
  ngaySinh: 'Ngày sinh',
  soHieu: 'Số hiệu',
  soCapBang: 'Số cấp bằng',
  hoTenNguoiNhan: 'Người nhận bằng',
  ngayCapBang: 'Ngày cấp bằng',
  ghiChuCapPhat: 'Ghi chú',
}

export default function BanGiaoVanBangChiTiet() {
  const khoaThiId = useSelector((item) => item.khoaThi.id)
  const [truongId, setTruongId] = useState(0)
  const [ngayBatDau, setNgayBatDau] = useState(firstDayStr)
  const [ngayKetThuc, setNgayKetThuc] = useState(lastDayStr)
  const [paging, setPaging] = useState({ skip: 0, take: 10, total: 0 })
  const [data, setData] = useState([])
  const [truongs, setTruongs] = useState([])

  useEffect(async () => {
    if (khoaThiId) {
      const rs = await reportProcessor.banGiaoVanBangChiTiet({
        khoaThiId,
        truongId,
        ngayBatDau,
        ngayKetThuc,
        skip: paging.skip,
        take: paging.take,
      })
      setData(rs.values)
      setPaging({ ...paging, total: rs.totalItemsCount })
    }
  }, [khoaThiId, truongId, ngayBatDau, ngayKetThuc])
  useEffect(async () => {
    const rs = await truongProcessor.getAll()
    setTruongs([{ id: 0, name: 'Tất cả' }, ...rs])
  }, [])
  const pagingChange = async (e) => {
    if (paging.skip !== e.first) {
      const rs = await reportProcessor.banGiaoVanBangChiTiet({
        khoaThiId,
        truongId,
        ngayBatDau,
        ngayKetThuc,
        skip: e.first,
        take: paging.take,
      })
      setData(rs.values)
      setPaging({ ...paging, skip: e.first })
    }
    if (paging.take !== e.rows) {
      const rs = await reportProcessor.banGiaoVanBangChiTiet({
        khoaThiId,
        truongId,
        ngayBatDau,
        ngayKetThuc,
        skip: paging.skip,
        take: e.rows,
      })
      setData(rs.values)
      setPaging({ ...paging, take: e.rows })
    }
  }

  return (
    <>
      <div className="flex flex-column">
        <div className="flex justify-content-between">
          <h4>Báo cáo bàn giao văn bằng chi tiết</h4>
          <div>
            <Button
              type="button"
              icon="pi pi-file-excel"
              className="p-button-success"
              onClick={() =>
                exportExcel(
                  data.map((item) => {
                    return {
                      ...item,
                    }
                  }),
                  'BanGiaoVanBangChiTiet',
                  mappingExport,
                )
              }
              disabled={data.length === 0}
              label="Xuất excel"
            />
          </div>
        </div>
        <div className="card">
          <Toolbar
            className="mb-4"
            right={
              <div className="flex vanbang-seacher-cotainer">
                <div className="mr-2 mb-2 flex align-items-center">
                  <b className="mr-1">Từ ngày:</b>
                  <Calendar
                    showIcon
                    showButtonBar
                    dateFormat="dd/mm/yy"
                    placeholder="Từ ngày"
                    value={ngayBatDau ? DDMMYYToDate(ngayBatDau) : undefined}
                    onChange={(e) => {
                      if (e.value) {
                        setNgayBatDau(dateToDDMMYY(e.value))
                      } else {
                        setNgayBatDau(undefined)
                      }
                    }}
                  />
                </div>
                <div className="mr-2 mb-2 flex align-items-center">
                  <b className="mr-1">Đến ngày:</b>
                  <Calendar
                    showIcon
                    showButtonBar
                    dateFormat="dd/mm/yy"
                    placeholder="Đến ngày"
                    value={ngayKetThuc ? DDMMYYToDate(ngayKetThuc) : undefined}
                    onChange={(e) => {
                      if (e.value) {
                        setNgayKetThuc(dateToDDMMYY(e.value))
                      } else {
                        setNgayKetThuc(undefined)
                      }
                    }}
                  />
                </div>
                <div>
                  <Dropdown
                    // tooltip="Năm"
                    tooltipOptions={{ position: 'top' }}
                    className="mr-2 mb-2"
                    optionLabel="name"
                    optionValue="id"
                    options={truongs}
                    value={truongId}
                    onChange={(e) => {
                      setTruongId(e.value)
                    }}
                    filter
                    filterBy="name"
                  />
                </div>
                <ComboDropdownKhoaThi />
              </div>
            }
          />
          <Table
            lazy
            data={data}
            column={[
              { value: 'hoTen', name: 'Họ tên người học', sortable: true },
              { value: 'ngaySinh', name: 'Ngày sinh', sortable: true },
              { value: 'soHieu', name: 'Số hiệu', sortable: true },
              { value: 'soCapBang', name: 'Số cấp bằng', sortable: true },
              { value: 'hoTenNguoiNhan', name: 'Người nhận bằng', sortable: true },
              { value: 'ngayCapBang', name: 'Ngày cấp bằng', sortable: true },
              { value: 'ghiChuCapPhat', name: 'Ghi chú', sortable: true },
            ]}
            {...paging}
            setPaging={pagingChange}
          />
        </div>
      </div>
    </>
  )
}
