import { createSlice } from '@reduxjs/toolkit'

const hoiDongReducer = createSlice({
  name: 'hoiDongs',
  initialState: { id: 0, datas: [] },
  reducers: {
    setHoiDongId: (state, action) => {
      return { ...state, id: action.payload }
    },
    setHoiDongs: (state, action) => {
      return { ...state, datas: action.payload }
    },
  },
})
const { actions, reducer } = hoiDongReducer
export const { setHoiDongId, setHoiDongs } = actions
export default reducer
