import { createSlice } from '@reduxjs/toolkit'

const monThiReducer = createSlice({
  name: 'monThi',
  initialState: { id: 0, datas: [] },
  reducers: {
    setMonThiId: (state, action) => {
      return { ...state, id: action.payload }
    },
    setMonThis: (state, action) => {
      return { ...state, datas: action.payload }
    },
  },
})
const { actions, reducer } = monThiReducer
export const { setMonThiId, setMonThis } = actions
export default reducer
